import React, {useState} from 'react';
import CustomToDos from './customHooks'

const handleSubmit=(e,todos,setTodos,input,setInput)=>{
  e.preventDefault()
  setTodos([...todos,{item: input,isCompleted:false}])
  setInput("")
}
function blah(value) {
  return value.isCompleted == false;
}
function App() {
  const [todos,setTodos]= useState([])
  const [input,setInput]= useState("")
  const toDoItems = todos.filter(blah)
  let ab = ""
  if(todos.length >=1){
    ab = todos.map(todo=>(
    <CustomToDos todoItem={todo.item} status={todo.isCompleted} />
  ))
    }
  return (

    <div className="App">

      Total Todos Remaining : {toDoItems.length} out of {todos.length}
      <form onSubmit={(e)=>handleSubmit(e,todos,setTodos,input,setInput)}>
        <input onChange={(e)=>setInput(e.target.value)} value={input}/>
        <button>Add Item</button>
      </form>
      <h3> The To Do items are: </h3>

      {ab}


    </div>
  );
}

export default App;
