import React, {useState} from 'react';
import CustomToDos from './customHooks'

const handleSubmit=(e,todos,setTodos)=>{
  e.preventDefault()
  setTodos([...todos,{item: "blahhhh",isCompleted:false}])
}

function App() {
  const [todos,setTodos]= useState([{item: "timesheet",isCompleted:false}])
  const [input,setInput]= useState("")
  return (
    <div className="App">
      <form onSubmit={(e)=>handleSubmit(e,todos,setTodos)}>
        <input onChange={(e)=>setInput(e.target.value)} value={input}/>
        <button>Add Item</button>
      </form>
      {todos.map(todo=>(
        <CustomToDos todoItem={todo.item} status={todo.isCompleted} />
      ))}


    </div>
  );
}

export default App;
